// Parse the blockchain API response and extract asset balances
const response = $input.all();

// Initialize asset balances
let ethBalance = 0;
let usdcBalance = 0;
let liquidUSDBalance = 0;

// Parse the response from the Query Vault Assets node
if (response && response.length > 0) {
  const data = response[0].json;

  // Check if data has assets array or similar structure
  if (data.assets && Array.isArray(data.assets)) {
    // Parse assets array
    data.assets.forEach(asset => {
      const symbol = asset.symbol || asset.name || '';
      const balance = parseFloat(asset.balance || asset.amount || 0);

      if (symbol.toUpperCase().includes('ETH') || symbol.toUpperCase() === 'WETH') {
        ethBalance += balance;
      } else if (symbol.toUpperCase().includes('USDC')) {
        usdcBalance += balance;
      } else if (symbol.toUpperCase().includes('LIQUIDUSD') || symbol.toUpperCase().includes('LIQUID_USD')) {
        liquidUSDBalance += balance;
      }
    });
  } else if (data.result && Array.isArray(data.result)) {
    // Alternative structure with result array
    data.result.forEach(asset => {
      const symbol = asset.symbol || asset.tokenSymbol || '';
      const balance = parseFloat(asset.balance || asset.value || 0);

      if (symbol.toUpperCase().includes('ETH') || symbol.toUpperCase() === 'WETH') {
        ethBalance += balance;
      } else if (symbol.toUpperCase().includes('USDC')) {
        usdcBalance += balance;
      } else if (symbol.toUpperCase().includes('LIQUIDUSD') || symbol.toUpperCase().includes('LIQUID_USD')) {
        liquidUSDBalance += balance;
      }
    });
  } else {
    // Try to extract from direct properties
    ethBalance = parseFloat(data.eth || data.ETH || data.weth || data.WETH || 0);
    usdcBalance = parseFloat(data.usdc || data.USDC || 0);
    liquidUSDBalance = parseFloat(data.liquidUSD || data.LiquidUSD || data.liquid_usd || 0);
  }
}

// Return the parsed asset balances
return [
  {
    json: {
      assets: {
        ETH: ethBalance,
        USDC: usdcBalance,
        LiquidUSD: liquidUSDBalance
      },
      totalAssets: ethBalance + usdcBalance + liquidUSDBalance,
      timestamp: new Date().toISOString()
    }
  }
];

// Get asset quantities from Parse Vault Assets node
const vaultData = $('Parse Vault Assets').first().json;
const ethQuantity = vaultData.ethQuantity || 0;
const usdcQuantity = vaultData.usdcQuantity || 0;
const liquidUsdQuantity = vaultData.liquidUsdQuantity || 0;
const borrowedAmount = vaultData.borrowedAmount || 0;

// Get prices from the three price fetch nodes
const ethPrice = $('Fetch ETH Price').first().json.price || 0;
const usdcPrice = $('Fetch USDC Price').first().json.price || 0;
const liquidUsdPrice = $('Fetch LiquidUSD Price').first().json.price || 0;

// Get LTV percentages from Workflow Configuration
const config = $('Workflow Configuration').first().json;
const ethLTV = config.ethLTV || 0;
const usdcLTV = config.usdcLTV || 0;
const liquidUsdLTV = config.liquidUsdLTV || 0;
const warningThreshold = config.warningThreshold || 0;

// Calculate market value for each asset
const ethMarketValue = ethQuantity * ethPrice;
const usdcMarketValue = usdcQuantity * usdcPrice;
const liquidUsdMarketValue = liquidUsdQuantity * liquidUsdPrice;

// Apply LTV percentages to get weighted values
const ethWeightedValue = ethMarketValue * (ethLTV / 100);
const usdcWeightedValue = usdcMarketValue * (usdcLTV / 100);
const liquidUsdWeightedValue = liquidUsdMarketValue * (liquidUsdLTV / 100);

// Calculate safety line (sum of weighted values)
const safetyLine = ethWeightedValue + usdcWeightedValue + liquidUsdWeightedValue;

// Calculate difference and risk metrics
const difference = safetyLine - borrowedAmount;
const utilizationPercentage = borrowedAmount > 0 ? (borrowedAmount / safetyLine) * 100 : 0;
const shouldAlert = difference <= warningThreshold;

// Return comprehensive calculation details
return [
  {
    json: {
      assets: {
        eth: {
          quantity: ethQuantity,
          price: ethPrice,
          marketValue: ethMarketValue,
          ltv: ethLTV,
          weightedValue: ethWeightedValue
        },
        usdc: {
          quantity: usdcQuantity,
          price: usdcPrice,
          marketValue: usdcMarketValue,
          ltv: usdcLTV,
          weightedValue: usdcWeightedValue
        },
        liquidUsd: {
          quantity: liquidUsdQuantity,
          price: liquidUsdPrice,
          marketValue: liquidUsdMarketValue,
          ltv: liquidUsdLTV,
          weightedValue: liquidUsdWeightedValue
        }
      },
      safetyLine: safetyLine,
      borrowedAmount: borrowedAmount,
      difference: difference,
      utilizationPercentage: utilizationPercentage,
      warningThreshold: warningThreshold,
      shouldAlert: shouldAlert,
      alertStatus: shouldAlert ? 'WARNING' : 'SAFE'
    }
  }
];

